INSERT INTO community_posts (category, title, content, views, likes, answers_count, parent_id, member_user_id, created_at, ishot) VALUES 
('투자정보', '주식 투자 초보자를 위한 완벽 가이드', '주식 투자를 시작하려는 분들을 위한 기본적인 가이드입니다. 먼저 투자 목표를 설정하고, 자신의 위험 감수 능력을 파악하는 것이 중요합니다. 분산투자, 장기투자, 감정 통제가 핵심입니다.', 1234, 89, 23, NULL, '투자고수', NOW() - INTERVAL '2 hours', true),
('부동산', '부동산 투자 vs 주식 투자, 어떤 것이 더 나을까?', '많은 분들이 궁금해하시는 부동산과 주식 투자의 장단점을 비교해보겠습니다. 부동산은 안정적이지만 유동성이 떨어지고, 주식은 변동성이 크지만 유동성이 높습니다.', 856, 67, 15, NULL, '부자되기', NOW() - INTERVAL '5 hours', false),
('성공사례', '월급쟁이 재테크 성공담 - 10년만에 1억 모으기', '평범한 월급쟁이였던 제가 어떻게 10년 만에 1억을 모을 수 있었는지 공유합니다. 가계부 작성, 강제 저축, 적립식 투자, 장기 관점 유지가 핵심이었습니다.', 2156, 234, 67, NULL, '절약왕', NOW() - INTERVAL '1 day', true),
('암호화폐', '코인 투자 실패담... 여러분은 이런 실수 하지 마세요', '코인 투자로 큰 손실을 본 경험을 공유하며, 같은 실수를 반복하지 않도록 도움이 되고자 합니다. 고점 올인, 레버리지 남용, 감정적 매매가 주요 실수였습니다.', 1678, 156, 89, NULL, '코인러버', NOW() - INTERVAL '2 days', false),
('질문답변', '펀드 추천 좀 해주세요! 투자 초보입니다', '투자 초보인데 어떤 펀드에 투자하면 좋을까요? 조언 부탁드립니다. 28세 직장인이고 매월 100만원 정도 투자 가능합니다.', 543, 23, 34, NULL, '투자초보', NOW() - INTERVAL '3 days', false); 